import Foundation

import struct Domain.Balance

extension Balance {
    
    var totalToString: String {
        return String(format: "%.2f", total)
    }
    
    var toUsdToString: String {
        if let rate = toUsdExchange?.last {
            let value = total * rate
            let numberFormatter = NumberFormatter()
            numberFormatter.numberStyle = .currency
            let usLocale = Locale(identifier: "en_US")
            numberFormatter.locale = usLocale
            return "â‰ˆ \(numberFormatter.string(from: NSNumber(value: value)) ?? "-")"
        }
        return "-"
    }
}
